<script lang="ts">
	import type { components } from '\$lib/api/types';
	import { Dialog, Button, Input, Spinner } from '\$lib/components/ui';
	import { api } from '\$lib/api/client';

	type CalendarConnection = components['schemas']['CalendarConnection'];
	type DiscoveredCalendar = components['schemas']['DiscoveredCalendar'];

	interface Props {
		open: boolean;
		onSuccess: (calendar: CalendarConnection) => void;
		onClose: () => void;
	}

	let { open = \$bindable(), onSuccess, onClose }: Props = \$props();

	type Step = 'credentials' | 'discovering' | 'selection' | 'saving';
	let step = \$state<Step>('credentials');
	let error = \$state<string | null>(null);

	// Credentials
	let serverUrl = \$state('');
	let username = \$state('');
	let password = \$state('');

	// Discovery results
	let discoveredCalendars = \$state<DiscoveredCalendar[]>([]);

	// Selection
	let selectedForAvailability = \$state<Set<string>>(new Set());
	let selectedForWriting = \$state<string | null>(null);

	function resetForm() {
		step = 'credentials';
		error = null;
		serverUrl = '';
		username = '';
		password = '';
		discoveredCalendars = [];
		selectedForAvailability = new Set();
		selectedForWriting = null;
	}

	function handleClose() {
		resetForm();
		onClose();
	}

	async function handleDiscover() {
		if (!serverUrl || !username || !password) {
			error = 'All fields are required';
			return;
		}

		step = 'discovering';
		error = null;

		const { data, error: apiError } = await api.POST('/calendars/discover', {
			body: { server_url: serverUrl, username, password }
		});

		if (apiError || !data) {
			error = 'Failed to connect to server';
			step = 'credentials';
			return;
		}

		if (!data.success) {
			error = data.error || 'Discovery failed';
			step = 'credentials';
			return;
		}

		if (data.calendars.length === 0) {
			error = 'No calendars found on this server';
			step = 'credentials';
			return;
		}

		discoveredCalendars = data.calendars;
		// Pre-select all calendars that support VEVENT
		for (const cal of discoveredCalendars) {
			const supportsEvents = !cal.supported_components?.length || 
				cal.supported_components.includes('VEVENT');
			if (supportsEvents) {
				selectedForAvailability.add(cal.url);
			}
		}
		selectedForAvailability = selectedForAvailability; // trigger reactivity
		step = 'selection';
	}

	function toggleAvailability(url: string) {
		if (selectedForAvailability.has(url)) {
			selectedForAvailability.delete(url);
		} else {
			selectedForAvailability.add(url);
		}
		selectedForAvailability = selectedForAvailability; // trigger reactivity
	}

	async function handleSave() {
		if (selectedForAvailability.size === 0) {
			error = 'Select at least one calendar for availability';
			return;
		}

		step = 'saving';
		error = null;

		const { data, error: apiError } = await api.POST('/calendars', {
			body: {
				server_url: serverUrl,
				username,
				password,
				calendar_urls: Array.from(selectedForAvailability),
				write_url: selectedForWriting || undefined
			}
		});

		if (apiError || !data) {
			error = 'Failed to save calendar connection';
			step = 'selection';
			return;
		}

		onSuccess(data);
		resetForm();
	}
</script>

