      <div class="p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
        <AvailabilityRuleForm
          {rule}
          onchange={(r) => updateRule(index, r)}
          onremove={rules.length > 1 ? () => removeRule(index) : undefined}
        />
